import React from 'react';
import { View, Text, StyleSheet, Image } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import Button from '../../components/Button'

export default function Home() {
        
    const navigation = useNavigation();

    navigation.setOptions({
      headerTitle: 'Início'
    })
      
 return (
   <View style={styles.container}>
       <Image style={styles.image} source={require('../../assets/orion.jpeg')}/>
       <View style={styles.box}>
           <Text style={styles.textContainer}>exemplo@email.com.br</Text>
       </View>

       <View style={styles.box}>
           <Text style={styles.textContainer}>••••••••</Text>
       </View>

       <Button onClick={()=> navigation.navigate('Adm')}/>
       
   </View>
  );
}

const styles = StyleSheet.create({
    container:{
        flex: 1,
        width: '100%',
        backgroundColor: '#FFF',
        alignItems: 'center',
        justifyContent: 'center'
    },   
    image:{
        width: 200,
        height: 200,
        borderRadius: 50
    },
    box:{
        backgroundColor: '#CECECF',
        borderRadius: 50,
        width: 300,
        height: 50,
        marginVertical: '5%',
        alignItems:'center',
        justifyContent: 'center',
    },    
    textContainer:{
        fontSize: 16,
        color: 'grey'
    }

});
