import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

export default function Adm({ navigation }) {

    navigation.setOptions({
        headerTitle: 'Painel de Controle'
      })

    return (
   <View style={styles.container}>
       <View>
            <Text style={styles.title}>PAINEL DE CONTROLE</Text>
       </View>

       <View style={styles.box}>
           <Text style={styles.textContainer}>Categorias</Text>
       </View>

       <View style={styles.box}>
           <Text style={styles.textContainer}>Produtos</Text>
       </View>

       <View style={styles.box}>
           <Text style={styles.textContainer}>Dados da loja</Text>
       </View>

       <View style={styles.box}>
           <Text style={styles.textContainer}>Sair</Text>
       </View>
   </View>
  );
}

const styles = StyleSheet.create({
    container:{
        flex: 1,
        width: '100%',
        backgroundColor: '#FFF',
        alignItems: 'center',
        justifyContent: 'center'
    },
    title:{
        fontFamily: 'PlayfairDisplay_400Regular',
        fontSize: 26,
        marginHorizontal: '1%'
    },
    box:{
        backgroundColor: '#000',
        borderRadius: 50,
        width: '60%',
        height: 45,
        marginVertical: '5%',
        alignItems:'center',
        justifyContent: 'center',
    },    
    textContainer:{
        fontSize: 16,
        marginHorizontal: '1%'
    }
    
});
